import Vue from 'vue'
import VueRouter from 'vue-router'
import HomeView from '@/components/common/HomeView.vue'
import LoginView from '@/views/LoginView.vue'
import IndexView from '@/views/IndexView.vue'
import UserList from '@/components/pages/user/UserList.vue'



Vue.use(VueRouter)

const routes = [
{
    path: '/',
    name: 'HomeView', //主路由
    component: HomeView,
    children: [
      {
        path: '/UserList',
        name: 'UserList',
        component: UserList
      }
    ]
  }, 
  {
    path: '/LoginView',
    name: 'LoginView',
    component: LoginView
  },
  {
    path: '/IndexView',
    name: 'IndexView',
    component: IndexView
  }
]

const router = new VueRouter({
  routes
})

export default router
