import Vue from 'vue'
import VueRouter from 'vue-router'
import HomeView from '@/components/common/HomeView.vue'
import LoginView from '@/components/common/LoginView.vue'
import IndexView from '@/components/pages/IndexView.vue'
import LookView from '@/components/common/LookView.vue'

import EmployNews from '@/components/pages/employ/EmployNews.vue'
import EmployList from '@/components/pages/employ/EmployList.vue'
import RentNews from '@/components/pages/rent/RentNews.vue'
import RentList from '@/components/pages/rent/RentList.vue'
import ExpertList from '@/components/pages/service/ExpertList.vue'
import ServiceList from '@/components/pages/service/ServiceList.vue'
import ProductNews from '@/components/pages/shopping/ProductNews.vue'
import ProductOrder from '@/components/pages/shopping/ProductOrder.vue'
//import ListingProducts from '@/components/pages/listing/ListingProducts.vue'





Vue.use(VueRouter)

const routes = [
{
    path: '/',  //会直接显示
    name: 'HomeView', //主路由
    component: HomeView,
    meta: { requiresAuth: true },
    children: [
      {
        path: '/',
        name: 'IndexView',
        component: IndexView

      },
      {
        path: '/EmployNews',
        name: 'EmployNews',
        component: EmployNews

      },
      {
        path: '/EmployList',
        name: 'EmployList',
        component: EmployList

      },
      {
        path: '/RentNews',
        name: 'RentNews',
        component: RentNews

      },
      {
        path: '/RentList',
        name: 'RentList',
        component: RentList

      },
      {
        path: '/ExpertList',
        name: 'ExpertList',
        component: ExpertList

      },
      {
        path: '/ServiceList',
        name: 'ServiceList',
        component: ServiceList

      },
      {
        path: '/ProductNews',
        name: 'ProductNews',
        component: ProductNews

      },
      {
        path: '/ProductOrder',
        name: 'ProductOrder',
        component: ProductOrder

      }
    ]
  }, 
  {
    path: '/LoginView',
    name: 'LoginView',
    component: LoginView
  },
  {
    path: '/LookView',
    name: 'LookView',
    component: LookView
  }
 /* {
    path: '/IndexView',
    name: 'IndexView',
    component: IndexView
  }*/
]

const router = new VueRouter({
  routes
})

router.beforeEach((to, from, next) => {  
  const user = store.state.user; // 假设你已经从Vuex中引入了store  

  // 判断是否需要认证  
  if (to.matched.some(record => record.meta.requiresAuth)) {  
    // 这里可以判断token是否存在，如果不存在则重定向到登录页  
    // 假设这里已经判断了token存在，并且用户已登录  
    if (!user) {  
      next({  
        path: '/login',  
        query: { redirect: to.fullPath }  
      });  
    } else {  
      // 判断用户角色是否满足要求  
      if (to.matched.some(record => record.meta.roles && !record.meta.roles.includes(user.role))) {  
        // 角色不匹配，重定向到另一个页面或首页  
        next({ name: 'home' });  
      } else {  
        next(); // 确保一定要调用 next()  
      }  
    }  
  } else {  
    next(); // 确保一定要调用 next()  
  }  
});  

export default router
