import Vue from 'vue'
import VueRouter from 'vue-router'
import HomeView from '@/components/common/HomeView.vue'
import LoginView from '@/views/LoginView.vue'
import IndexView from '@/components/pages/message/IndexView.vue'
import MessageList from '@/components/pages/message/MessageList.vue'
import PlantData from '@/components/pages/plant/PlantData.vue'
import PlantPlan from '@/components/pages/plant/PlantPlan.vue'
import ExpertList from '@/components/pages/service/ExpertList.vue'
import ServiceList from '@/components/pages/service/ServiceList.vue'
import ProductNews from '@/components/pages/shopping/ProductNews.vue'
import ProductOrder from '@/components/pages/shopping/ProductOrder.vue'
import UserList from '@/components/pages/user/UserList.vue'



Vue.use(VueRouter)

const routes = [
{
    path: '/',
    name: 'HomeView', //主路由
    component: HomeView,
    children: [
      {
        path: '/IndexView',
        name: 'IndexView',
        component: IndexView
      },
      {
        path: '/MessageList',
        name: 'MessageList',
        component: MessageList
      },
      {
        path: '/PlantData',
        name: 'PlantData',
        component: PlantData
      },
      {
        path: '/PlantPlan',
        name: 'PlantPlan',
        component: PlantPlan
      },
      {
        path: '/ExpertList',
        name: 'ExpertList',
        component: ExpertList
      },
      {
        path: '/ServiceList',
        name: 'ServiceList',
        component: ServiceList
      },
      {
        path: '/ProductNews',
        name: 'ProductNews',
        component: ProductNews
      },
      {
        path: '/ProductOrder',
        name: 'ProductOrder',
        component: ProductOrder
      },
      {
        path: '/UserList',
        name: 'UserList',
        component: UserList
      }
    ]
  }, 


  {
    path: '/LoginView',
    name: 'LoginView',
    component: LoginView
  },
 /* {
    path: '/IndexView',
    name: 'IndexView',
    component: IndexView
  }*/
]

const router = new VueRouter({
  routes
})

export default router
