import SoldList from '@/components/pages/acquisition/SoldList.vue'
*/

const originalPush = VueRouter.prototype.push
VueRouter.prototype.push = function push (location, onResolve, onReject) {
  if (onResolve || onReject){
    return originalPush.call(this, location, onResolve, onReject)
  }
  return originalPush.call(this, location).catch(err => err)
}

const routes = [
  {  
    path: '/',  
    name: 'LoginView',  
    component: LoginView,
    meta: { requiresAuth: false },
  },  
  // 注册页面，可以设置一个特定的路由路径  

  // 首页，也设置一个特定的路由路径  
  {  
    path: '/HomeView',  
    name: 'HomeView',  
    component: HomeView, 
    // 可以添加meta字段来控制路由的权限，例如是否需要登录后才能访问  
    meta: { requiresAuth: true },

  /*children: [      //用户菜单栏
      {
        path: '/IndexView',
        name: 'IndexView',
        component: IndexView

      },
      {
        path: '/EmployNews',
        name: 'EmployNews',
        component: EmployNews

      },
      {
        path: '/RegistrationList',
        name: 'RegistrationList',
        component: RegistrationList

      },
      {
        path: '/EmployList',
        name: 'EmployList',
        component: EmployList

      },
      {
        path: '/RentNews',
        name: 'RentNews',
        component: RentNews

      },
      {
        path: '/RentalList',
        name: 'RentalList',
        component: RentalList

      },
      {
        path: '/RentList',
        name: 'RentList',
        component: RentList

      },
      {
        path: '/ExpertList',
        name: 'ExpertList',
        component: ExpertList

      },
      {
        path: '/ServiceList',
        name: 'ServiceList',
        component: ServiceList

      },
      {
        path: '/ProductNews',
        name: 'ProductNews',
        component: ProductNews

      },
      {
        path: '/ProductOrder',
        name: 'ProductOrder',
        component: ProductOrder

      },
      {
        path: '/SoldList',
        name: 'SoldList',
        component: SoldList

      }
    ]
    */
    children: [      //管理员菜单栏
    {
      path: '/IndexView1',
      name: 'IndexView1',
      component: IndexView1

    },
    {
      path: '/NewsView',
      name: 'NewsView',
      component: NewsView

    },
    {
      path: '/UserList',
      name: 'UserList',
      component: UserList

    },
    ]
  },  
]

// 确保使用VueRouter 
Vue.use(VueRouter)

const router = new VueRouter({
  routes
})

router.beforeEach((to, from, next) => {
  console.log("跳转页from:"+from.fullPath + "  to:"+to.fullPath);
  let user = getUser();
  console.log(2);
  const isAuthenticated = (user === undefined);
  console.log(isAuthenticated);
  console.log(to.meta.requiresAuth);
  if (to.matched.some(record => record.meta.requiresAuth)) {
    if (isAuthenticated) {
      // 如果用户未登录且访问的是需要登录的页面，则重定向到登录页面
      next({path: '/'});
      console.log(4+"   "+to.fullPath);
    } else {
      console.log(3+"   "+to.fullPath);
      // 如果用户已登录或访问的不是需要登录的页面，正常导航
      next();
    }
  } else {
    console.log(5+"   "+to.fullPath);
    if(to.fullPath == "/") {
      let user = getUser();
      if(!(user === undefined)) {
        console.log("已经登陆了，欢迎："+user.username);
        next({path: '/IndexView1'});
      }
    } else {
      next();
    }
  }


})

function getUser() {
   let user = window.sessionStorage.getItem('user');
   console.log(user);
   return user;
}



export default router
